import React, {useState} from 'react'
import Flashcard from './Flashcard'
import FlashcardModal from './FlashcardModal';
// import {Button} from 'react-bootstrap'

class FlashcardContainer extends React.Component{

    // const [flashcards, setFlashcards] = useState()

    state= {
        flashcards: [],
        user: 1,
        showModal: false
    }

    toggleModal = () => this.setState( prevState => ({showModal:!prevState.showModal}));

    addFlashcard = (newFlashcard) => {
        this.setState({
            flashcards: [...this.state.flashcards, newFlashcard]
        })
    }

    componentDidMount(){

        fetch("http://localhost:3000/api/v1/flashcards")
        .then(resp => resp.json())
        .then(flashcards => {
            this.setState({ flashcards })
            }
        )
    }
    render() {
        return (
            <>
                <h1>My FlashCards</h1>
                <div className='card-grid'>
                    {this.state.flashcards.map(card => {
                        if(card.user_id === this.state.user){
                            return <Flashcard {...card} key={card.id} />
                        }
                    })}
                </div>
                <Button variant="info" onClick={this.toggleModal}>Create New Flashcard</Button>
                <div>
                    <FlashcardModal 
                        show={this.state.showModal}
                        toggleModal={this.toggleModal}
                        addFlashcard={this.addFlashcard}
                    />
                </div>
            </>
        )
    }
}

export default FlashcardContainer
