import React from 'react'

const ScoreCard = (props) => {

    let date = new Date(props.created_at);
    let showDate =  (date.getMonth()+1) + '-'+ date.getDate() + '-' + date.getFullYear()
    let qNum

    if(props.game_type === "Quiz"){
        qNum = 5
    }else{
        qNum = 26
    }

    return (
        <div>
            <p>On {showDate}, you got {((props.score/qNum)*100).toFixed(2)}% correct in the {props.game_type} game.</p>
        </div>  
    )
}

export default ScoreCard
