import React from 'react'
import {Button, Modal, Form} from 'react-bootstrap'

class FlashcardModal extends React.Component {

  state = {
    user_id: 1,
    id: "",
    name: "",
    details_front: "",
    details_back: ""
  }

//   componentDidUpdate(prevProps){
//     if (this.props.name === prevProps.name){
//       const {user_id,name,details_front,details_back} = this.props
//       const newState =  { 
//         user_id: user_id,
//         name: name,
//         details_front: details_front,
//         details_back: details_back
//       }
//       this.setState(newState)
//     }
//   } 

  handleChange = (e) =>{
    this.setState({[e.target.name]:e.target.value})
  }

  handleClick = () => {
    const {id, user_id,name,details_front,details_back,} = this.state
    const currentId = id
    const serverData = {
        method:(currentId ? 'PATCH' : 'POST'),
        headers:{
          'Content-Type':'application/json',
          'Accept':'application/json'
        },
        body:JSON.stringify({
            user_id,name,details_front,details_back
        })
      }
    fetch(`http://localhost:3000/api/v1/flashcards/${currentId}`, serverData)
    .then(resp=>resp.json())
    .then(flashcard =>{ 
        currentId ? this.props.addFlashcard(flashcard) : this.props.updateFlashcard(flashcard)

        this.props.toggleModal()

    })
  }

  render(){
    const {show,toggleModal} = this.props
    const {user_id,name,details_front,details_back,} = this.state
    return (
      <>
        <Modal show={show} onHide={toggleModal}>
          <Modal.Header closeButton>
            <Modal.Title>Flashcard Form</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <Form>
              <Form.Group controlId="name">
                <Form.Label>Name</Form.Label>
                <Form.Control type="text" placeholder="Enter card name" name="name" value={name} onChange={this.handleChange} />
              </Form.Group>
              <Form.Group controlId="details_front">
                <Form.Label>Front</Form.Label>
                <Form.Control type="text" placeholder="Enter card front" name="details_front" value={details_front} onChange={this.handleChange} />
              </Form.Group>
              <Form.Group controlId="details_back">
                <Form.Label>Back</Form.Label>
                <Form.Control type="text" placeholder="Enter card back" name="details_back" value={details_back} onChange={this.handleChange} />
              </Form.Group>
            </Form>
          </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={toggleModal}>
              Close
            </Button>
            <Button variant="primary" onClick={this.handleClick}>
              Save Changes
            </Button>
          </Modal.Footer>
        </Modal>
      </>
    );
  }
}

export default FlashcardModal;
